package it.cnr.isti.hlt.processfast.core;import it.cnr.isti.hlt.processfast.data.ReadableDictionary;import it.cnr.isti.hlt.processfast.data.StorageManager;/** * The runtime context usable when calling transformations and actions * on a partitionable dataset. *  * @author Tiziano Fagni (tiziano.fagni@isti.cnr.it) * @since 1.0.0 */public interface TaskDataContext {	/**	 * Get the parent task set data dictionary. This dictionary is shared among all	 * tasks in the parent task set.	 * 	 * @return The task set data dictionary.	 */	ReadableDictionary getTasksSetDataDictionary();		/**	 * Get the data shared by the task which is the owner of this context.	 * 	 * @return The data shared by the task which is owner of this context.	 */	TaskSharedData getTaskSharedData();	/**	 * Get the storage manager available on the system. The storage manager and	 * all its contained data structures are shared among	 * all tasks and tasks sets defined in a specific program.	 *	 * @return The storage manager available on the system.	 */	StorageManager getStorageManager();}